import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';

const Product = ({ product }) => {
  return (
    <ProductWrapper className="col-9 mx-auto col-md-6 col-lg-3 my-3">
      <div className="card">
        <div className="img-container p-5" onClick={() => console.log('Image clicked')}>
          <Link to="/details">
            <img src={product.img} alt="product-img" className="card-img-top" />
          </Link>
          <button className="cart-btn" disabled={product.inCart ? true : false} onClick={() => console.log(product.inCart)}>

            {product.inCart ? (<p className="text-capitalize mb-0 disabled">in cart</p>) : (<i className="fas fa-cart-plus"></i>)}

          </button>
        </div>
        <div className="cart-footer d-flex justify-content-between">
          <p className="align-self-center mb-0">
            {product.title}
          </p>
          <h5 className="text-blue font-italic mb-0 mr-2">
            <span className="mr-2">$</span>
            {product.price}
          </h5>
        </div>
      </div>
    </ProductWrapper >
  );
}

export default Product;

const ProductWrapper = styled.div`

`
