import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import { useContext } from 'react';
import { ProductContext } from "../contexts/ProductContext";

const Product = ({ product }) => {
  const { handleDetails, addCart, openModel } = useContext(ProductContext);
  const { id } = product;
  return (
    <ProductWrapper className="col-9 mx-auto col-md-6 col-lg-3 my-3">
      <div className="card">
        <div className="img-container p-5" onClick={() => handleDetails(id)}>
          <Link to="/details">
            <img src={product.img} alt="" className="card-img-top" />
          </Link>
          <button
            onClick={() => {
              addCart(id)
              openModel(id)
            }}
            className="cart-btn" disabled={product.inCart ? true : false}>
            {product.inCart ? (
              <p className="text-capitalize mb-0" disabled>
                in cart</p>
            ) : (
                <i className="fas fa-cart-plus" />
              )}
          </button>
          <div className="card-footer d-flex justify-content-between">
            <p className="align-self-center mb-0">{product.title}</p>
            <h5 className="text-blue font-italic mb-0">
              <span className="mr-1">$</span>
              {product.price}
            </h5>
          </div>
        </div>

      </div>

    </ProductWrapper >
  );
}
//! PropTypes
Product.propTypes = {
  product: PropTypes.shape({
    id: PropTypes.number,
    img: PropTypes.string,
    title: PropTypes.string,
    price: PropTypes.number,
    inCart: PropTypes.bool
  }).isRequired
}

export default Product;

//! Styled Elements
const ProductWrapper = styled.div`
  .card {
    border-color: transparent;
    transition: all 1s linear;
  }
  .card-footer {
    background: transparent;
    border-top: transparent;
    transition: all 1s linear;
  }
  &:hover {
    .card {
      border: 0.04rem solid rgba(0, 0, 0, 0.2);
      box-shadow: 2px 2px 5px 0px rgba(0, 0, 0, 0.2);
    }
    .card-footer {
      background: rgba(247, 247, 247);
    }
  }
  .img-container {
    position: relative;
    overflow: hidden;
  }
  .card-img-top {
    transition: all 1s linear;
  }
  .img-container:hover .card-img-top {
    transform: scale(1.2);
  }
  .cart-btn {
    position: absolute;
    bottom: 0;
    right: 0;
    padding: 0.2rem 0.4rem;
    background: var(--lightBlue);
    border: none;
    color: var(--mainWhite);
    font-size: 1.4rem;
    border-radius: 0.5rem 0 0 0;
    transform: translate(100%, 100%);
    transition: all 1s ease-in-out;
  }
  .img-container:hover .cart-btn {
    transform: translate(0, 0);
  }
  .cart-btn:hover {
    color: var(--mainBlue);
    cursor: pointer;
  }
`
